    DBMS_OUTPUT.PUT_LINE( DataCell(cur."16", myMax) );
    DBMS_OUTPUT.PUT_LINE( DataCell(cur."17", myMax) );
    DBMS_OUTPUT.PUT_LINE( DataCell(cur."18", myMax) );
