DECLARE
  cnt NUMBER(10);
  BG  VARCHAR2(10);
  -- 1. declare a list type
TYPE assoc_array_type
IS
  TABLE OF VARCHAR2(15) INDEX BY VARCHAR2(15);
  g_const_array assoc_array_type;
TYPE COUNTRY_LIST_TYPE
IS
  TABLE OF VARCHAR2(15);
  -- 2. declare the variable of the list
  V_COUNTRY_VALUES COUNTRY_LIST_TYPE;
  -- 3. optional variable to store single values
  --V_COUNTRY_VALUE VARCHAR2(15);
  country VARCHAR2(15);
  SLA     VARCHAR2(15);
  RD      DATE;
  RD1     VARCHAR2(15);
  CURSOR reporting_date
  IS
    (SELECT *
    FROM
      (SELECT DISTINCT reporting_date
      FROM scb_job_stats
      WHERE TO_CHAR(reporting_date,'YYYYMM') = TO_CHAR(sysdate,'YYYYMM')
      ORDER BY reporting_date
      )
    );
  TYPE reporting_date_tab
IS
  TABLE OF reporting_date%ROWTYPE;
  reporting_date_rec reporting_date_tab;
BEGIN
  g_const_array('JP') := '01:30';
  g_const_array('KR') := '01:30';
  g_const_array('MY') := '02:30';
  g_const_array('SG') := '02:30';
  g_const_array('CN') := '02:30';
  g_const_array('HK') := '02:30';
  g_const_array('TW') := '02:30';
  g_const_array('PH') := '02:30';
  g_const_array('BN') := '02:30';
  g_const_array('ID') := '03:30';
  g_const_array('TH') := '03:30';
  g_const_array('VN') := '03:30';
  g_const_array('BD') := '04:30';
  g_const_array('NP') := '04:45';
  g_const_array('IN') := '05:00';
  g_const_array('LK') := '05:00';
  g_const_array('PK') := '05:30';
  g_const_array('AE') := '06:30';
  g_const_array('OM') := '06:30';
  g_const_array('BH') := '07:30';
  g_const_array('JO') := '08:30';
  g_const_array('QA') := '07:30';
  g_const_array('KE') := '07:30';
  g_const_array('BW') := '08:30';
  g_const_array('ZA') := '08:30';
  g_const_array('ZW') := '08:30';
  g_const_array('NG') := '09:30';
  g_const_array('EU') := '10:30';
  g_const_array('GH') := '10:30';
  g_const_array('US') := '15:30';
  -- 4. initialize the list of values to be iterated in a for-loop
  V_COUNTRY_VALUES := COUNTRY_LIST_TYPE('JP','KR','MY','SG','CN','HK','TW','PH','BN','ID','TH','VN','BD','NP','IN','PK','LK','AE','OM','BH','JO','QA','KE','BW','ZA','ZW', 'NG', 'EU', 'GH', 'US');
  --V_COUNTRY_VALUES := COUNTRY_LIST_TYPE('JP','KR','MY');
  -- 5. iterating over the values
  DBMS_OUTPUT.PUT_LINE('<html xmlns="http://www.w3.org/1999/xhtml">');
  DBMS_OUTPUT.PUT_LINE('<HEAD>');
  DBMS_OUTPUT.PUT_LINE('<title>ALM REPORTS</title>');
  DBMS_OUTPUT.PUT_LINE('</HEAD>');
  DBMS_OUTPUT.PUT_LINE('<BODY>');
  DBMS_OUTPUT.PUT_LINE('');
  DBMS_OUTPUT.PUT_LINE('<table cellspacing="0" cellpadding="4" align="Left" rules="all" bordercolor ="black" cellpadding="2" cellspacing="1" border="1" id="MasterPageContent_Tabs_Panel1_gvDashboard" style="border-collapse:collapse;">');
  DBMS_OUTPUT.PUT_LINE('<tr class="theme_header" align="left" style="color:Black;background:#3DAE38;border-color:#000000;border-width:1pt;border-style:solid;height:18px;">');
  DBMS_OUTPUT.PUT_LINE('<th valign="top" scope="col" style="white-space:nowrap;">Description</th>');
  OPEN reporting_date;
  FETCH reporting_date bulk collect INTO reporting_date_rec;
  CLOSE reporting_date;
  FOR l IN reporting_date_rec.FIRST..reporting_date_rec.LAST
  LOOP
    RD1:=TO_CHAR(reporting_date_rec(l).reporting_date,'YYYYMMDD');
    --DBMS_OUTPUT.PUT_LINE(RD1);
    DBMS_OUTPUT.PUT_LINE('<th valign="top" scope="col">'||RD1||'</th>');
  END LOOP;
  DBMS_OUTPUT.PUT_LINE('</tr>');
  FOR j IN V_COUNTRY_VALUES.FIRST..V_COUNTRY_VALUES.LAST
  LOOP
    --DBMS_OUTPUT.PUT_LINE();
    COUNTRY:=V_COUNTRY_VALUES(j);
    DBMS_OUTPUT.PUT_LINE('<tr class="tableItem">        
<td style="white-space:nowrap;">&nbsp;&nbsp;&nbsp;'||COUNTRY||'  <sup><font size="0.8em" color="Red">*</font></sup></td>');
    FOR k IN reporting_date_rec.FIRST..reporting_date_rec.LAST
    LOOP
      RD:=reporting_date_rec(k).reporting_date;
      --DBMS_OUTPUT.PUT_LINE(COUNTRY||'-'||RD);
      SLA:=g_const_array(COUNTRY);
      --DBMS_OUTPUT.PUT_LINE('SLA: '||SLA);
      SELECT COUNT(*)
      INTO cnt
      FROM scb_job_stats
      WHERE reporting_date                                                        = RD
      AND scb_country                                                             =COUNTRY
      AND job_success                                                             ='Y'
      AND job_name                                                                ='scb_pack_obi_daily_load.scb_obi_rep_load_rbau'
      AND ( (scb_country                                                         IN ('AE','OM','BH','JO','QA')
      AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('friday'))
      OR (scb_country                                                            IN ('BD')
      AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('friday','saturday'))
      OR (scb_country                                                            IN ('NP')
      AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('saturday'))
      OR (scb_country                                                            IN ('JP','KR','MY','SG','CN','HK','TW','PH','BN','ID','TH','VN','NP','IN','LK','PK','KE','BW','ZA','ZW','NG','EU','GH','US')
      AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('saturday','sunday')) );
      IF (cnt                                                                     = 1) THEN
        SELECT
          CASE
            WHEN TO_CHAR(MIN(end_time),'HH24MI') < SLA
            THEN 'GREEN'
            ELSE 'RED'
          END
        INTO BG
        FROM scb_job_stats
        WHERE reporting_date                                                        = RD
        AND scb_country                                                             =COUNTRY
        AND job_success                                                             ='Y'
        AND job_name                                                                ='scb_pack_obi_daily_load.scb_obi_rep_load_rbau'
        AND ( (scb_country                                                         IN ('AE','OM','BH','JO','QA')
        AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('friday'))
        OR (scb_country                                                            IN ('BD')
        AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('friday','saturday'))
        OR (scb_country                                                            IN ('NP')
        AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('saturday'))
        OR (scb_country                                                            IN ('JP','KR','MY','SG','CN','HK','TW','PH','BN','ID','TH','VN','NP','IN','LK','PK','KE','BW','ZA','ZW','NG','EU','GH','US')
        AND trim(TO_CHAR(reporting_date,'day','NLS_DATE_LANGUAGE = American')) NOT IN ('saturday','sunday')) );
      ELSE
        BG := 'SILVER';
      END IF;
      --DBMS_OUTPUT.PUT_LINE('COUNTRY: '||COUNTRY||' - RD: '||RD||' - SLA: '||SLA||' - BG: '||BG);
      DBMS_OUTPUT.PUT_LINE('<td style="color:black;background-color:'||BG||';text-align:center;">');
      DBMS_OUTPUT.PUT_LINE('</td>');
      --EXCEPTION
      -- WHEN NO_DATA_FOUND THEN
      --  BG := 'SILVER';
      --WHEN OTHERS THEN
      --  DBMS_OUTPUT.PUT_LINE('error');
      --END;
    END LOOP;
    DBMS_OUTPUT.PUT_LINE('</tr>');
  END LOOP;
  DBMS_OUTPUT.PUT_LINE('</TABLE>');
  DBMS_OUTPUT.PUT_LINE('');
  DBMS_OUTPUT.PUT_LINE('</BODY>');
  DBMS_OUTPUT.PUT_LINE('');
  DBMS_OUTPUT.PUT_LINE('</HTML>');
  DBMS_OUTPUT.PUT_LINE('');
END;

DECLARE
  l_report CLOB := dbms_sqltune.report_sql_monitor( type=>'HTML', sql_id => :SQL_ID
    , SESSION_ID => :SID, SESSION_SERIAL => :SERIAL#, SQL_EXEC_ID => :SQL_EXEC_ID );
  l_offset PLS_INTEGER := 1;
  l_length PLS_INTEGER := NVL(dbms_lob.getlength(l_report),0);
  l_chunk CONSTANT PLS_INTEGER := 8191;
BEGIN
  dbms_output.enable(NULL);
  --dbms_output.put_line('Len: '||l_length);
  WHILE l_offset < l_length LOOP
    --dbms_output.put_line('Off: '||l_offset);
    dbms_output.put_line(dbms_lob.substr( l_report, l_chunk, l_offset));
    l_offset := l_offset + l_chunk;
  END LOOP;
  --dbms_output.put_line('');
END;

declare
dbid number;
instance_id number;
begin
select dbid into dbid from v$database;
select instance_number into instance_id from v$instance;
--instance_id := 2;
dbms_output.enable(500000);
dbms_output.put_line('<PRE>');
for rc in ( select output from 
   table(dbms_workload_repository.ash_report_text( dbid,nvl(:INST_ID,instance_id),to_date(:start,'yyyymmddhh24mi'),
   to_date(:end,'yyyymmddhh24mi'),0,0,:SID))) loop
   dbms_output.put_line(rc.output);
end loop;
dbms_output.put_line('</PRE>') ;
end;
